import { createSlice } from '@reduxjs/toolkit';

export const detailPageSlice = createSlice({
    name: "detailsPage",
    initialState: {
        detailsCount: 0,
    },
    reducers: {
        increment(state, action) {
            return {
                ...state,
                detailsCount: state.detailsCount + 1
            }

        }
    }
})
