import React, {useEffect} from 'react'


export const getproduct = () => {
    const products = [];
    const url = 'https://yalantis-react-school-api.yalantis.com/api/v1/products';
    useEffect(() => {
        fetch(url)
            .then((result) => result.json())
            .then((result) => {
                products = result.items
            })
    }, [])
        return products;
}
