import { createSlice } from '@reduxjs/toolkit';

export const detailPageSlice = createSlice({
    name: "detailsPage",
    initialState: {
        detailsCount: 0,
        detailsSum: 0,
    },
    reducers: {
        increment(state, action) {
            state.detailsCount += 1,
            state.detailsSum += action.payload.value
        },
        decrement(state, action) {
            state.detailsCount -= 1,
            state.detailsSum = action.payload.value
        },
        handleChange(state, action) {
            state.detailsCount = Number(action.payload.value)
        },
    }
})
