import React, { useState } from 'react';
import './editForm.css';
import ProductForm from '../ProductForm/ProductForm';
import axios from 'axios';
import { useSelector } from 'react-redux';
import { Portal } from '../../../common/components/Portal/Portal';
import Modla  from 'react-modal';



const EditForm = ({form}) => {
    const [isOpen, setIsOpen] = useState(false)
    const store = useSelector(state => state)
    const {formSlice} = store
    const handleSubmit = (values) => {
        axios.patch(`http://localhost:3000/products/${formSlice.id}`, values)
            .then(res => {
                if (res.ok) {
                    return res.json()
                }
            }).then(jsonRes =>jsonRes)
    }
    return (
        <Portal className="modla-portal" element="div">
            <Modal isOpen={isOpen}>
                <div className="editForm" ref={form}>
                    <ProductForm 
                        form={form}
                        state={formSlice.productById}
                        handleSubmit={handleSubmit} 
                    />
                </div>
            </Modal>
        </Portal>
    )
}

export default EditForm;
