import React from 'react';
import {setPage} from '../../features/productsSlice';
import { useDispatch, useSelector } from 'react-redux';


const PaginationElement = ({number, first, second}) => {
    const dispatch = useDispatch() 
    const store = useSelector(state => state.productsSlice)
    const handleChange = () => {
        if(parseInt(number)) {
            if(number >= 6) {
                first.classList.add('numberOfPageButton')
                first.style.display = 'flex'
            }else if(number < 6) {
                first.style.display = 'none'
            }
            dispatch(setPage({page: number}))
        }
    }
    return (
        <div className=
            {
                number === 'first' ? 'threeDotsFirst' :
                number === 'second' ? 'threeDotsSecond' :
                number === store.page ? "currentPage": "numberOfPageButton"
            }
            onClick={handleChange}
        > 
            {
                number === 'first' || number === 'second' 
                ? '...'
                : number
            }
        </div>
    )
}

export default PaginationElement
