import React, { useRef } from 'react';
import {setPage} from '../../features/productsSlice';
import { useDispatch, useSelector } from 'react-redux';


const PaginationElement = (props) => {
    const dispatch = useDispatch()
    const dotsAfterFirst = document.querySelector('.threeDots')
    const dotsBeforeLast = document.querySelector('.fourDots') 
    const store = useSelector(state => state.productsSlice)
    const handleChange = () => {
        if(parseInt(props.number)) {
            dispatch(setPage({page: props.number}))
            if(store.page >= 6) {
                dotsAfterFirst.style.display = 'block'
                dotsAfterFirst.style.className="numberOfPageButton"
            }else if(store.page <= 6) {
                dotsAfterFirst.style.display = 'none'   
            }
        }
    }
    return (
        <div className={
                props.number === '....' ? "numberOfPageButton fourDots" :
                props.number === '...' ? "threeDots" :
                props.class ? "hiddenPaginElement numberOfPageButton" :
                props.number === store.page ? "currentPage": "numberOfPageButton"
            }
            onClick={handleChange}
        > 
            {props.number === '....' ? props.number.replace('.', '', 1) : props.number}
        </div>
    )
}

export default PaginationElement
