import React from "react";
import { useSelector, useDispatch } from "react-redux";
import { handleCheckbox } from '../../features/productsSlice';

const FilterOrigin = () => {
    // Redux 
    const store = useSelector(state => state.productsSlice)
    const dispatch = useDispatch()
    const { origins } = store; 

    return (
        <div className="selectOrigin">
            <div className="originTilte">Origins:</div>
            {
                origins.map((origin, id) => 
                    <div className="originList" key={id}>
                        <label htmlFor="origin" className="originName">
                            {origin.toUpperCase()}
                        </label>
                        <input  
                            type="checkbox" 
                            name={origin} 
                            className="origin"
                            defaultChecked={store.selected}
                            onChange={(e) => dispatch(handleCheckbox())}   
                        />
                    </div>    
                )
            }
        </div>
    )
}

export default FilterOrigin
