import React from 'react'
import DetailsBody from '../../components/DetailPageComponents/DetailsBody/DetailsBody'
import HeaderOfPage from '../../components/Header/HeaderOfPage'
// Redux
import { useSelector, useDispatch } from 'react-redux';
import {countGenerallyAddedProducts,
        addProductsToCartPage,
        decreaseProductFromCart,  } from '../../features/productsSlice';
import { handleChange, addToInput,  decreaseFromInput } from '../../features/inputSlice';
import { useState } from 'react';


const ProductsDetail = () => {
    const store = useSelector(state => state)
    const dispatch = useDispatch()
    const { amountAddedProducts, sumOfPricesAddedProducts, product } = store.productsSlice
    const increment = () => {
        if (count >= 0) {

           dispatch(addProductsToCartPage({array: [product]}))
        } 
    }
    const decrement = () => {
        if (count >= 1) {
            setCount(count - 1)
            dispatch(decreaseProductFromCart())
        }
    }
    const handleChanges = (event) => {
        dispatch(handleChange({value: event.target.value}))
    }
    const detailsPageAddToCart = () => {
        const sum = count * product.price;
        const array = [];
        for(let i = 0; i < count; i++) {
            array.push(product)
        }
        dispatch(countGenerallyAddedProducts({value: count, price: sum}))
        dispatch(addProductsToCartPage({product: array}))
    }

    return (
        <div className="detailsContainer">
            <HeaderOfPage 
                    count={amountAddedProducts} sum={sumOfPricesAddedProducts} 
            />
            <DetailsBody 
                    handleChange={handleChanges}
                    product={product} 
                    increment={increment} 
                    decrement={decrement}
                    count={store.inputSlice.value}
                    addToCart={detailsPageAddToCart}
            />
        </div>
    )
}

export default ProductsDetail;
