import { call, put, takeEvery } from 'redux-saga/effects';
import { getProducts } from '../api/getProducts';
import { fetchProducts } from '../features/productsSlice';
// import { useSelector } from 'react-redux';

function* workerFetchProducts() {
    const store = useSelector(state => state)
    const {productsSlice} = store
    const {perPage, page, origins, minPrice, maxPrice} = productsSlice
    const data = yield call(getProducts(perPage, page, origins, minPrice, maxPrice));
    yield put(fetchProducts(data));
}

export function* watchFetchProducts() {
    yield takeEvery(fetchProducts, workerFetchProducts);
}
