import React, {useState} from 'react';
import { Link } from 'react-router-dom'
import Products from '../components/products/Products'
import HeaderOfPage from '../components/header/HeaderOfPage'

const MainPage = () => {
    const [sum, setSum] = useState(0)
    const [count, setCount] = useState(0)
    const addToCard = (value) => {
        setSum(sum + Number(value))
        setCount(count + 1);
    }
    return (
        <div className="container">
            <HeaderOfPage count={count} sum={sum} />
            <Products addToCard={addToCard} goToDetailPage={
                <Link to="/products" className="toDetailPage">MORE DETAIL</Link>
            } />
        </div>
    )
}

export default MainPage;
