import React from 'react';
import ProductForm from '../Forms/ProductForm/ProductForm';
import axios from 'axios';
import { useSelector } from 'react-redux';
import { Portal } from '../../common/components/Portal/Portal';

export const EditProductModal = ({open}) => {
    const store = useSelector(state => state)
    const {formSlice} = store
    const handleSubmit = (values) => {
        axios.patch(`http://localhost:3000/products/${formSlice.id}`, values)
            .then(res => {
                if (res.ok) {
                    return res.json()
                }
            }).then(jsonRes =>jsonRes)
    }
    return (
        <Portal className="modal-portal" element="div">
            <Modal className="editForm">
                <ProductForm 
                    state={formSlice.productById}
                    handleSubmit={handleSubmit} 
                />
            </Modal>
        </Portal>
    )
}
