import { useSelector } from "react-redux";

const store = useSelector(state => state.productsSlice)
export const getProducts = (page) => {
    return ( 
        fetch(`https://yalantis-react-school-api.yalantis.com/api/v1/products?page=${page}&perPage=${store.perPage}`)
            .then((result) => result.json())
            .then((result) => {
                return result;
            })
            .catch((error) => {
                return error
            })
    )
}
