import { createSlice } from '@reduxjs/toolkit';


export const mainPageSlice = createSlice({
    name: 'handleAddToCartClick',
    initialState: {
        amountAddedProducts: 0,
        sumOfPricesAddedProducts: 0,
        product: {}, 
        cartPageProducts: [],
        cartPageSetProducts: [],
    }, 
    reducers: {
        addToCartCount(state, action) {
            const { price } = action.payload.payload
            return {
                ...state, 
                amountAddedProducts: state.amountAddedProducts + 1,
                sumOfPricesAddedProducts: state.sumOfPricesAddedProducts + price,
            }
        },
        setDetailsPageProduct(state, action) {
            const { product } = action.payload.payload
            return {
                ...state,
                product: product
            }
        },
        addProductsToCartPage(state, action) {
            return {
                ...state,
                cartPageProducts: [...state.cartPageProducts, product],
            }
        },
        setProductsCartPage(state, action) {
            const { product } = action.payload.payload
            return {
                ...state,
                cartPageSetProducts: [...state.cartPageSetProducts, product]
            }
        }
    }
})
