import React from 'react';
// import {  } from '../../features/productsSlice';
import { useSelector } from 'react-redux';
import './pagination.css';

const Pagination = () => {
    const store =  useSelector(state => state.productsSlice)

    const range = () => {
        const range = []
        for(let i = 1; i <= store.range; i++) {
            range.push(i)
        }
        return range;
    }
    const rangeOfPages = range()

    return (
        <div className="pagination">
            {
               rangeOfPages.map(number =>
                    <div className="numberOfPageButton">{number}</div>
               ) 
            }
        </div>
    )
}

export default Pagination
