import React, {useState, useReducer} from 'react'


export const Context = React.createContext();

export const ACTIONS = {
    ADD_TO_CART: 'add-to-cart',
    ADD_PRODUCT_TO_DETAILS: 'add-product-to-details',
    INCREMENT: 'increment',
    DECREMENT: 'decrement',
    SUM_OF_ADDED: 'sum_of_added',

}

const reducer = (state, action) => {
    switch(action.type) {
        case ACTIONS.ADD_TO_CART:
            state[0] = state[0] + 1
            state[1] = state[1] + action.payload.value
            return state
        case ACTIONS.ADD_PRODUCT_TO_DETAILS:
            state[2] = action.payload.product
            return state
        case ACTIONS.INCREMENT:
            state[4] = state[4] + action.payload.price
            state[3] = state[3] + 1
            return state
        case ACTIONS.DECREMENT:
            state[4] = state[4] - action.payload.price
            state[3] = state[3] - 1
            return state
        default:
            return state;
    }
}

export const HandleDetail = (props) => {
    const [state, dispatch] = useReducer(reducer, [0, 0, {}, 0, 0]) 

    const [cart, setCart] = useState([])
    const values = {
        state, dispatch, cart, setCart
    }
    return (
        <Context.Provider value={values}>
            {props.children}
        </Context.Provider>
    )
}
