import React, { useState } from 'react'
import { useDispatch, useSelector } from 'react-redux';
import {setMinMaxPrice} from '../../features/productsSlice';


const FilterPrices = () => {
    const dispatch = useDispatch()  
    const store = useSelector(state => state)
    const {productsSlice} = store  
    const handleMin = (e) => {
        const value = e.target.value
        dispatch(setMinMaxPrice({min: value}))
    }
    const handleMax = (e) => {
        const value = e.target.value
        dispatch(setMinMaxPrice({max: value}))
    }

    return (
        <div className="min_max">
            <label htmlFor="prices" className="min_max_label">Price: </label>
            <input 
                type="text" 
                className="min"
                placeholder="min" 
                value={productsSlice.pricesMinMax.min}
                onChange={handleMin}
            />
            <div className="dash">-</div>
            <input 
                type="text" 
                className="max" 
                placeholder="max"
                value={productsSlice.pricesMinMax.max}
                onChange={handleMax} 
            />
        </div>
    )
}

export default FilterPrices
