import React from 'react';
import './editForm.css';
import ProductForm from '../ProductForm/ProductForm';
import axios from 'axios';
import { useSelector } from 'react-redux';
import { Portal } from '../../../common/components/Portal/Portal';
import Modal  from 'react-modal';



const EditForm = ({isOpen}) => {
    const store = useSelector(state => state)
    const {formSlice} = store
    const handleSubmit = (values) => {
        axios.patch(`http://localhost:3000/products/${formSlice.id}`, values)
            .then(res => {
                if (res.ok) {
                    return res.json()
                }
            }).then(jsonRes =>jsonRes)
    }
    return (
        <Portal className="modal-portal" element="div">
            <Modal isOpen={isOpen} title="Edit product">
                <div className="editForm">
                    <ProductForm 
                        state={formSlice.productById}
                        handleSubmit={handleSubmit} 
                    />
                </div>
            </Modal>
        </Portal>
    )
}

export default EditForm;
