import React, {useState} from 'react';
import {setPage, 
        showSelectedNumberProductsPerPage, 
} from '../../features/productsSlice';
import { useDispatch } from 'react-redux';


const PaginationElement = (props) => {
    const [state, setState] = useState('numberOfPageButton')
    const dispatch = useDispatch()
    const handleChange = (event) => {
        if(event.target.className === 'currentPage') {
            setState("numberOfPageButton")
        }else {
            setState("currentPage")
            dispatch(setPage({page: props.number}))
            dispatch(showSelectedNumberProductsPerPage())
        }  
    }
    return (
        <div className={state} 
             onClick={handleChange}>
          {props.number}
        </div>
    )
}

export default PaginationElement
