import { createSlice } from '@reduxjs/toolkit';


export const mainPageSlice = createSlice({
    name: 'counter',
    initialState: {
        amountAddedProducts: 0,
        sumOfPricesAddedProducts: 0,
        product: {}, 
        cartPageProducts: [],
        cartPageSetProducts: [],
    }, 
    reducers: {
        addToCart(state, action) {
            const { price, product } = action.payload.payload
            return {
                ...state, 
                amountAddedProducts: state.amountAddedProducts + 1,
                sumOfPricesAddedProducts: state.sumOfPricesAddedProducts + price,
                cartPageProducts: [...state.cartPageProducts, product],
                cartPageSetProducts: 
                    !state.cartPageProducts.includes(product) ?
                        [...state.cartPageSetProducts, product]
                    : [...state.cartPageSetProducts]
            }
        },
        detailsPage(state, action) {
            const { product } = action.payload.payload
            return {
                ...state,
                product: product
            }
        }
    }
})
