import React, { useEffect, useState } from 'react';
import { Formik, Form, useField } from 'formik';
import * as Yup from 'yup';
import './addOrEditForm.css';
import { useDispatch, useSelector } from 'react-redux';
import { fetchCreatedProductsById } from '../../../features/formSlice';


const MyTextInput = ({label, ...props}) => {
  const [field, meta] = useField(props);
  return (
    <div className="text-iput">
      <label htmlFor={props.id || props.name} className="label"></label>
      <input className="inputField" {...field} {...props} />
      {meta.touched && meta.error ? (
        <div className="errorMessage">{meta.error}</div>
      ): null}
    </div>
  )
}
const MySelect = ({label, ...props}) => {
  const [field, meta] = useField(props);
  return (
    <div>
      <label htmlFor={props.id || props.name} className="label"></label>
      <select {...field} {...props} />
      {meta.touched && meta.error ? (
        <div className="errorMessage">{meta.error}</div>
      ): null}
    </div>
  )
}

const FormForAddAndEditProduct = ({handleSubmit, form}) => {
    const [state, setState] = useState({name: '', price: '', origin: ''})
    const store = useSelector(state => state.formSlice)
    const dispatch = useDispatch()
    useEffect(() => {
      dispatch(fetchCreatedProductsById(store.id))
    }, [store.productById, dispatch, store.id])
    console.log(store.productById);
    return (
      <div className="form">
        <Formik 
          initialValues={state}
          validationSchema={Yup.object({
            name: Yup.string()
            .min(3, 'Required Min 3 symbols!!!')
            .max(20, 'Max available 20 symbols!!!')
            .required('Required!!!'),
            price: Yup.number()
              .typeError('Type error. Must be a number!!!')
              .min(0, 'Negaive numbers doesn\'t allowed!!!')
              .required('Reqiured!!!'),
            origin: Yup.string()
              .oneOf(['ASIA', 'USA', 'EUROPE', 'AFRICA'], "Invalid Origin")
              .required('Required!!!')
          })}
          onSubmit={(values, {setSubmitting, resetForm} )=> {
              setSubmitting(true)
              handleSubmit(values)
              setSubmitting(false)
              setTimeout(() => {
                window.location.reload()
              }, 1000)
          }}
        >
        {({ isSubmitting, dirty, resetForm }) => (
          <Form className="addProductForm">
            <MyTextInput
                label="name"
                name="name" 
                type="text" 
                autoComplete="off"
                placeholder="Product Name" 
            />
            <MyTextInput
                label="price"
                name="price" 
                type="text" 
                className="inputField" 
                autoComplete="off"
                placeholder="Product Price" 
            />
            <MySelect label="origin" name="origin" className="originsField">
              <option value="" className="optionOrigin">Select origin</option>
              <option value="ASIA" className="optionOrigin">ASIA</option>
              <option value="USA" className="optionOrigin">USA</option>
              <option value="EUROPE" className="optionOrigin">EUROPE</option>
              <option value="AFRICA" className="optionOrigin">AFRICA</option>
            </MySelect>
            <button 
              type="submit" 
              className="submitForm"
              disabled={isSubmitting || !dirty}
            >
              Submit
            </button>
            <button 
                type="reset" 
                className="resetFormButton"
                onClick={() => {
                  resetForm({values: state})
                }}
            >
                RESET
            </button>
            <button 
                type="button" 
                className="cancelChangesButton"
                onClick={() => {
                  form.current.style.display = ''
                  resetForm({values: state})
                }}
            >
                CANCEL
            </button>
          </Form>
        )}
        </Formik>
      </div>
    )
}

export default FormForAddAndEditProduct
