import React from 'react';
import './editForm.css';
import FormForAddOrEditProduct from '../FormForAddOrEditProduct';
import axios from 'axios';
import { useDispatch, useSelector } from 'react-redux';
import { resetEditForm } from '../../../../features/formSlice';


const EditForm = ({form}) => {
    const store = useSelector(state => state.formSlice)
    const handleSubmit = (values) => {
        axios.patch(`http://localhost:3000/products/${store.id}`, values)
            .then(res => res.json()).then(jsonRes =>jsonRes)
    }
    return (
        <div className="editForm" ref={form}>
            <FormForAddOrEditProduct 
                form={form}
                state={store.productById}
                handleSubmit={handleSubmit} 
            />
            <button 
                type="reset" 
                className="resetFormButton"
                onClick={() => {

                }}
            >
                RESET
            </button>
            <button 
                type="button" 
                className="cancelChangesButton"
                onClick={() => {
                  form.current.style.display = ''

                }}
            >
                CANCEL
            </button>
        </div>
    )
}

export default EditForm;
