    const [sum, setSum] = useState(0)
    const [count, setCount] = useState(0)
    const [detailsCount, setDetailsCount] = useState(0)
    const [product, setProduct] = useState({})
    const [sumOfAdded, setSumOfAdded] = useState(0);
