import React from 'react';
import {ReactComponent as OpenFormSVG} from '../../../assets/openForm.svg';
import ProductForm from '../ProductForm/ProductForm';
import axios from 'axios';
import './createProductForm.css';
import Modal from 'react-modal';
import { Portal } from '../../../common/components/Portal/Portal';


const CreateProductForm = () => {
  const handleSubmit = (values) => {
    const newProduct = {
      name: values.name,
      price: values.price,
      origin: values.origin
    }
    axios.post('http://localhost:3001/products/create', newProduct)
  } 
  return (
    <Portal className="portal-modal" element="div">
      <Modal title="Create Product"> 
        <div className="addNewProductForm">
          <div className="formToAddNewProduct">
            <ProductForm 
              handleSubmit={handleSubmit} 
              state={{name: '', price: '', origin: ''}}
            />
          </div>
        </div>
      </Modal>
    </Portal>
  )
}

export default CreateProductForm
