import React, { useState } from 'react'
import { useDispatch } from 'react-redux';
import { setMinAndMaxPrice } from '../../features/productsSlice';


const FilterPrices = () => {
    const dispatch = useDispatch()    
    const [state, setValue] = useState({min: '', max: ''})
    console.log(state);
    const handleMin = (e) => {
        const {value} = e.target.value
        setValue({...state, min: value})
        dispatch(setMinAndMaxPrice({min: value}))
    }
    const handleMax = (e) => {
        const {value} = e.target.value
        setValue({...state, max: value})
        dispatch(setMinAndMaxPrice({max: value}))
    }

    return (
        <div className="min_max">
            <label htmlFor="prices" className="min_max_label">Price: </label>
            <input 
                type="text" 
                className="min"
                placeholder="min" 
                value={state.min}
                onChange={handleMin}
            />
            <div className="dash">-</div>
            <input 
                type="text" 
                className="max" 
                placeholder="max"
                value={state.max}
                onChange={handleMax} 
            />
        </div>
    )
}

export default FilterPrices
