import { useSelector } from 'react-redux';
import { call, put, select, takeEvery } from 'redux-saga/effects';
import { getProducts } from '../api/getProducts';
import { fetchProducts, loadData } from '../features/productsSlice';


function* workerFetchProducts() {
    const store = yield select(useSelector(state => state.productsSlice))
    const data = yield call(getProducts, );
    yield put(fetchProducts(data));
}

export function* watchFetchProducts() {
    yield takeEvery(loadData.type, workerFetchProducts);
}
