import React from 'react';
import { useDispatch, useSelector } from 'react-redux';


const TableHead = () => {
    return (
        <thead className="orderedTableHead">
            <tr>
                <th className="tableTitleColumn">Date</th>
                <th className="tableTitleColumn">Name</th>
                <th className="tableTitleColumn">Origin</th>
                <th className="tableTitleColumn">Amount</th>
                <th className="tableTitleColumn">Price</th>
            </tr>
       </thead>
    )
}
const TableBody = ({date}) => {
    const rows = product.orderedProducts.map((product, id) => {
        return (
            <tr key={id} className="orderedTableBodyElement">
                <td className="ordersTableRow">{date}</td>
                <td className="ordersTableRow">{product.name}</td>
                <td className="ordersTableRow">{product.origin.toUpperCase()}</td>
                <td className="ordersTableRow">{2}</td>
                <td className="ordersTableRow">{product.price}$</td>
            </tr>
        )
    })
    return <tbody className="orderedTableBody">{rows}</tbody>
}

const OrderedProductsBody = ({product, date}) => { 
    const store = useSelector(state => state)
    const {productsSlice, formSlice} = store
    const dispatch = useDispatch()
    useEffect(() => {
        dispatch(fetchOrderProductsByNickname(formSlice.nickname))
    }, [formSlice.nickname, dispatch])
    return (
        <div className="orderedProductsBody">
            <table className="orderedProductsTableTitle">
                <TableHead />    
                <TableBody  date={date} />
            </table>
        </div>
    )
}

export default OrderedProductsBody
