import React, { useContext } from 'react'
import {ACTIONS, Context} from '../context/Context'
import DetailsBody from '../components/DetailPageComponents/body/DetailsBody'
import HeaderOfPage from '../components/MainPageComponents/header/HeaderOfPage'


const ProductsDetail = (price) => {
    const value = useContext(Context)
    const increment = () => {
        value.dispatch({ type: ACTIONS.INCREMENT })
        if (value.state[3] >= 0) {
            value.dispatch({ type: ACTIONS.INCREMENT, payload: {price: value.state[2]} })
        }

    }
    const decrement = (price) => {
        value.dispatch({ type: ACTIONS.DECREMENT })
        if (value.state[3] >= 1) {
            value.dispatch({ type: ACTIONS.DECREMENT, payload: {price: value.state[2]} })
        }

    }
    const handleDetailAddToCart = () => {
        value.dispatch({ type: ACTIONS.DETAIL_ADD_TO_CART })
        value.dispatch({ type: ACTIONS.DETAIL_ADD_TO_CART })
    }

    return (
        <div className="detailsContainer">
            <HeaderOfPage count={value.state[0]} sum={value.state[1]} />
            <DetailsBody 
                    product={value.state[2]} 
                    increment={increment} 
                    decrement={decrement}
                    count={value.state[3]}
                    addToCart={handleDetailAddToCart}
            />
        </div>
    )
}

export default ProductsDetail;
