import React, {useState} from 'react';
import Products from '../components/MainPageComponents/products/Products'
import HeaderOfPage from '../components/MainPageComponents/header/HeaderOfPage'
import '../styles/MainPage/header.css'
import '../styles/MainPage/body.css'
import { Context } from '../context/detailHandler'

const MainPage = () => {
    const [sum, setSum] = useState(0)
    const [count, setCount] = useState(0)
    const addToCard = (value) => {
        setCount(count + 1);
        setSum(sum + Number(value));
    }
    return (
        <Context.Provider value={
            count, sum
        }>
            <div className="container">
                <HeaderOfPage count={count} sum={sum} />
                <Products addToCard={addToCard} />
            </div>
        </Context.Provider>
    )
}

export default MainPage;
