import React, {useState, useReducer} from 'react'


export const Context = React.createContext();

export const ACTIONS = {
    ADD_TO_CART: 'add-to-cart',
    ADD_PRODUCT_TO_DETAILS: 'add-product-to-details',
    INCREMENT: 'increment',
    DECREMENT: 'decrement',
    SUM_OF_ADDED: 'sum_of_added',
    DETAIL_ADD_TO_CART: 'detail-add-to-cart'
}

const reducer = (state, action) => {
    switch(action.type) {
        case ACTIONS.ADD_TO_CART:
            return [state[0] + 1, state[1] + action.payload.value]
        case ACTIONS.ADD_PRODUCT_TO_DETAILS:
            return [state[0], state[1], action.payload.product]
        case ACTIONS.INCREMENT:
            return [
                state[0], state[1], state[2], state[3] + 1, state[4] + state[2].price
            ]
        case ACTIONS.DECREMENT:
            return [ 
                state[0], state[1], state[2], state[3] - 1, state[4] + state[2].price
            ]
        case ACTIONS.DETAIL_ADD_TO_CART:
            return state
        default:
            return state;
    }
}

export const HandleDetail = (props) => {
    const [state, dispatch] = useReducer(reducer, [0, 0, {}, 0, 0]) // count, sum, product, detailsCount, detailsSum 

    const [cart, setCart] = useState([])
    const values = {
        state, dispatch, cart, setCart
    }
    return (
        <Context.Provider value={values}>
            {props.children}
        </Context.Provider>
    )
}
