import React, {useEffect} from 'react'
// Element of Body 
import ProductElement from './ProductElement'
// Api 
import {getProducts} from '../../api/getProducts'
// Local Imports
import './body.css'
import bigphoto from '../../assets/bigphoto.jpg'
import { recieveProducts, fetchProductsStart } from '../../features/apiSlice'
import { useDispatch, useSelector } from 'react-redux'


const Products = (props) => {
    const store = useSelector(state => state.apiSlice)
    const dispatch = useDispatch()
    useEffect(() => {
        dispatch(fetchProductsStart())
        getProducts().then((res) => {
            dispatch(recieveProducts({products: res}))
        })
    }, [])
    return (
        <div className="productsContainer">
            <div className="bigPhoto">
                <img src={bigphoto} alt="" />
            </div>
            {
                store.products.map(element =>  
                    <div key={element.id} className="product"> 
                        <ProductElement
                                product={element} 
                                addToCart={props.addToCart} 
                                addProductToDetails={props.addProductToDetails} 
                        />
                    </div>
                )
            }
        </div>
    )
}

export default Products;
