import React, { useState } from 'react'
import { Redirect } from 'react-router-dom';
import { useSelector, useDispatch } from 'react-redux'
import CartBodyElement from './CartBodyElement'
import { setDateOfOrder, setNickname } from '../../features/formSlice';
import './bodyCartPage.css'
import axios from 'axios';

const BodyCartPage = (props) => {
    const store = useSelector(state => state)
    const dispatch = useDispatch()
    const { cartPageSetProducts, sumOfPricesAddedProducts } = store.productsSlice
    const [reloaded, setReload] = useState(false)
    const handleBuyClick = () => {
        dispatch(setDateOfOrder({date: new Date().toDateString()}))
        axios.post("http://localhost:3001/products/order", {
            date: store.setDateOfOrder, 
            nickname: store.nickname, 
            cartPageSetProducts
        })
        setTimeout(() => {
            setReload(true)
        }, 600)   
    }
    return (
        <div className="cartPageBody">  
            {reloaded ? <Redirect to="/products/ordereds" /> :
                <div className="some">
                    {  
                        cartPageSetProducts.map((product, index) =>          
                                <div key={index}>
                                    <CartBodyElement 
                                        product={product} 
                                        deleteProduct={props.deleteProduct}
                                    />
                                </div>
                            )
                    }
                    <div className="totalSum" onClick={handleBuyClick}>
                        Order: {sumOfPricesAddedProducts}$
                    </div>
                </div>
                }
        </div>
    )
}

export default BodyCartPage;
